import React, {PureComponent} from 'react';
import {TouchableOpacity} from 'react-native';
import {Body, Text, Thumbnail, View} from 'native-base';

export default class ListDataItem extends PureComponent {
  constructor (props) {
    super (props);
    this.data = props.data;
    this._handlePress = this._handlePress.bind (this);
  }

  _handlePress () {
    const {url, title} = this.data;
    this.props.onPress ({url, title});
  }

  render () {
    return (
      <TouchableOpacity
        onPress={this._handlePress}
        style={{flexDirection: 'row'}}
        activeOpacity={0.5}
      >
        <Thumbnail
          style={{backgroundColor: '#eee', alignSelf: 'center'}}
          square
          large
          source={{
            cache: 'force-cache',
            uri: this.data.urlToImage != null
              ? 'http://image-api.suckup.de/image-api.php?file=' +
                  this.data.urlToImage +
                  '&quality=25'
              : 'data:image/png;base64,',
          }}
        />
        <Body>
          <Text style={{fontSize: 16}} numberOfLines={2}>
            {this.data.title}
          </Text>
          <Text note numberOfLines={3}>{this.data.description}</Text>
          <View
            style={{flex: 1, flexDirection: 'row', marginTop: 8, marginLeft: 8}}
          >
            <Text note>{this.data.source.name}</Text>
          </View>
        </Body>
      </TouchableOpacity>
    );
  }
}
